tinymce.PluginManager.add('ptnimage', function (editor, url) {
    // Add a button that opens a window
    editor.ui.registry.addButton('ptnimage', {
        icon: 'image',
        text: 'Add From Library',
        tooltip: 'Add Image from Resource Library',
        onAction: function () {
            // Open window
            var url = '/?contentType=popup&ewCmd=ImageLib&targetForm=EditContent&targetField=' + editor.id

            $('#modal-' + editor.id + ' .modal-body').load(url, function (result) {
                var myModal = new bootstrap.Modal(document.getElementById('modal-' + editor.id), {
                    keyboard: false
                })
                myModal.show();
            });

//            editor.windowManager.open({
//                title: 'Example plugin',
//                body: [
//                    { type: 'textbox', name: 'title', label: 'Title' }
//                ],
//                onsubmit: function (e) {
//                    // Insert content when the window form is submitted
//                    editor.insertContent('Title: ' + e.data.title);
//                }
//            });
        }
    });

    // Adds a menu item to the tools menu
    editor.ui.registry.addMenuItem('ptnimage', {
        text: 'Pick Library Image',
        icon: 'image',
        context: 'Insert',
        onAction: function () {
            // Open window
            var url = '/?contentType=popup&ewCmd=ImageLib&targetForm=EditContent&targetField=' + editor.id

            $('#modal-' + editor.id).load(url, function (result) {
                var myModal = new bootstrap.Modal(document.getElementById('modal-' + editor.id), {
                    keyboard: false
                })
                myModal.show();
            });
        }
    });
});