tinymce.PluginManager.add('ptnmodule', function (editor, url) {
    // Add a button that opens a window
    editor.ui.registry.addButton('ptnmodule', {
        icon: 'image',
        text: 'Add Module Box',
        tooltip: 'Add Inline Module',
        onAction: function () {
            // Open window
            var url = '/?contentType=popup&ewCmd=ImageLib&targetForm=EditContent&targetField=' + editor.id

            $('#modal-' + editor.id + ' .modal-body').load(url, function (result) {
                var myModal = new bootstrap.Modal(document.getElementById('modal-' + editor.id), {
                    keyboard: false
                })
                myModal.show();
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.ui.registry.addMenuItem('ptnmodule', {
        text: 'Pick Library Image',
        icon: 'image',
        context: 'Insert',
        onAction: function () {
            // Open window
            var url = '/?contentType=popup&ewCmd=ImageLib&targetForm=EditContent&targetField=' + editor.id

            $('#modal-' + editor.id).load(url, function (result) {
                var myModal = new bootstrap.Modal(document.getElementById('modal-' + editor.id), {
                    keyboard: false
                })
                myModal.show();
            });
        }
    });
});