tinymce.PluginManager.add('ewimage', function (editor, url) {
    // Add a button that opens a window
    editor.ui.registry.addButton('ewimage', {
        icon: 'image',
        text: 'Add',
        tooltip: 'Add Image from Resource Library',
        onclick: function () {
            // Open window
            var url = '/?contentType=popup&ewCmd=ImageLib&targetForm=EditContent&targetField=' + editor.id

            $('#modal-' + editor.id).load(url, function (result) {
                $('#modal-' + editor.id).modal({ show: true });
            });

//            editor.windowManager.open({
//                title: 'Example plugin',
//                body: [
//                    { type: 'textbox', name: 'title', label: 'Title' }
//                ],
//                onsubmit: function (e) {
//                    // Insert content when the window form is submitted
//                    editor.insertContent('Title: ' + e.data.title);
//                }
//            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('ewimage', {
        text: 'Insert EW Image',
        icon: 'image',
        context: 'insert',
        onclick: function () {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'Pick Image',
                url: '?contentType=popup&ewCmd=ImageLib&targetForm=EditContent&targetField=cContentDisplay&targetClass=display',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});